package com.fr.design.mainframe;import javax.swing.JComponent;import com.fr.form.FormElementCaseContainerProvider;/** * Author : Shockway * Date: 13-7-15 * Time: 上午10:28 */public interface BaseJForm extends JTemplateProvider{		public static final String XML_TAG = "JForm";	public static final int FORM_TAB = 0;	public static final int ELEMENTCASE_TAB = 1;	public static final int ELEMENTCASE_CHANGE_TAB = 2;		/**	 * 刷新所有控件	 */	public void refreshAllNameWidgets();    /**     * 刷新参数     */    public void populateParameter();		/**	 * 刷新选中的控件	 */	public void refreshSelectedWidget();		/**	 * 获取当前的Target	 */	public Object getTarget();		/**	 * 执行撤销	 * 	 * @param o 之前保存的状态	 */	public void applyUndoState4Form(BaseUndoState o);		/**	 * 获取当前编辑的组件	 */	public JComponent getEditingPane();    /**     * 只在Form和ElementCase之间切换     * @param index   切换位置     */	public void tabChanged(int index);    /**     *  在Form和ElementCase, 以及ElementCase和ElementCase之间切换     * @param index  切换位置     * @param ecContainer      ElementCase所在container     */	public void tabChanged(int index, FormElementCaseContainerProvider ecContainer);}