package com.fr.design.form.parameter;import com.fr.design.designer.creator.XButton;import com.fr.design.designer.creator.XCreator;import com.fr.form.parameter.FormSubmitButton;import java.awt.*;/** * 提交按钮 * @editor zhou * @since 2012-3-23下午3:36:00 */public class XFormSubmit extends XButton {    public XFormSubmit(FormSubmitButton widget, Dimension initSize) {        super(widget, initSize);    }    /**     * 是否有查询按钮     * @param xCreator  控件或容器     * @return  有无查询按钮     */    public boolean SearchQueryCreators(XCreator xCreator) {        return true;    }    /**     * 该组件是否可以拖入表单主体     * @return 是则返回true     */    public boolean canEnterIntoAdaptPane(){        return false;    }    @Override    protected String getIconName() {        return "preview_16.png";    }}